require_once("../../../wp-load.php");
error_log( "Hello from ft image script" );

$i = 0;
foreach( $posts as $post ){
    // error_log( $i . " " . $post['post_name'] );
    // error_log( $i . " " . $post['guid'] );
    $post_id =  get_posts(  array( 'name' => $post['post_name'] ) )[0]->ID;

    $file = $post['guid'];
    $filename = basename( $file );

    $upload_file = wp_upload_bits( $filename, null, file_get_contents( $file ) );

    if( !$upload_file['error'] ){
      $wp_filetype = wp_check_filetype($filename, null );
      $attachment = array( 
        'post_mime_type' => $wp_filetype['type'],
        'post_title' => preg_replace('/\.[^.]+$/', '', $filename),
        'post_content' => '',
      );
      $attachment_id = wp_insert_attachment( $attachment, $upload_file['file'], $post_id );
      if( !is_wp_error( $attachment_id ) ){
        require_once( ABSPATH . 'wp-admin' . '/includes/image.php' );
        $attachemt_data = wp_generate_attachment_metadata( $attachment_id, $upload_file['file'] );
      
        wp_update_attachment_metadata( $attachment_id, $attachemt_data );

        set_post_thumbnail( $post_id, $attachment_id );

        error_log("Inserted and attached image " . $file);
      }
    }else{
      error_log("Error with post " . $post['post_name'] . " image: " . $file);
    }
    $i++;
}